package javafirstpack;

/**
 * 
 */
public class IfElseIf {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n =39;
		if(n % 2 == 0) 
			System.out.println(" Love Yourself");
		else if(n == 10)
			System.out.println("Love others");
		else if(n % 2 != 0)
			System.out.println("You Mean the World To ME");
		else
			System.out.println("Be Happy");
		System.out.println("Be Good TO others");
			
	}
}


