package javafirstpack;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Welcome to my happy diary");
		int n=1;
		switch(n) {
		case 1:
			System.out.println("we are happy when we are together");
			break;
		case 2: 
			System.out.println("we are apart and still happy");
			break;
		}
		
	}

}
